USE michelonr;

DROP TABLE IF EXISTS Users;
DROP TABLE IF EXISTS Artworks;
DROP TABLE IF EXISTS Labels;
DROP TABLE IF EXISTS ArtworkLabels;
DROP TABLE IF EXISTS ArtworkDetails;
DROP TABLE IF EXISTS Artshows;
DROP TABLE IF EXISTS ArtshowPrenotations;

CREATE TABLE Users (
    id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    username VARCHAR(30) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL,
    name VARCHAR(30) NOT NULL,
    lastname VARCHAR(30) NOT NULL,
    is_admin BOOLEAN NOT NULL,
    image VARCHAR(100) UNIQUE,
    birth_date DATE,
    birth_place VARCHAR(30),
    biography VARCHAR(1000),
    experience VARCHAR(1000)
) ENGINE=INNODB;

CREATE TABLE Artworks (
    id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    title VARCHAR(100) NOT NULL,
    main_image VARCHAR(100) NOT NULL UNIQUE,
    description VARCHAR(1000) NOT NULL,
    height FLOAT,
    width FLOAT,
    length FLOAT,
    start_date DATE,
    end_date DATE,
    upload_time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    id_artist INT NOT NULL REFERENCES Users(id) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=INNODB;

CREATE TABLE Labels (
    label VARCHAR(20) PRIMARY KEY NOT NULL
) ENGINE=INNODB;

CREATE TABLE ArtworkLabels (
    id_artwork INT NOT NULL REFERENCES Artwork(id) ON UPDATE CASCADE ON DELETE CASCADE,
    label VARCHAR(20) NOT NULL REFERENCES Labels(label) ON UPDATE CASCADE ON DELETE CASCADE,
    PRIMARY KEY (id_artwork, label)
) ENGINE=INNODB;

CREATE TABLE ArtworkDetails (
    id_artwork INT NOT NULL REFERENCES Artwork(id) ON UPDATE CASCADE ON DELETE CASCADE,
    image VARCHAR(100) NOT NULL UNIQUE,
    PRIMARY KEY (id_artwork, image)
) ENGINE=INNODB;

CREATE TABLE Artshows (
    id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    title VARCHAR(100) NOT NULL,
    description VARCHAR(1000) NOT NULL,
    image VARCHAR(100) UNIQUE NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
) ENGINE=INNODB;

CREATE TABLE ArtshowPrenotations (
    id_artshow INT NOT NULL REFERENCES Artshow(id) ON UPDATE CASCADE ON DELETE CASCADE,
    id_artist INT NOT NULL REFERENCES Users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (id_artshow, id_artist)
) ENGINE=INNODB;









